interface:
class TodoStore {
  constructor();
  getUserByUsername(username: string): User | undefined;
  getUserById(id: string): User | undefined;
  createTodo(userId: string, data: Omit<Todo, 'id' | 'userId' | 'createdAt' | 'updatedAt'>): Todo;
  getTodo(id: string): Todo | undefined;
  getTodosByUser(userId: string, filters?: {
      completed?: boolean
      priority?: 'low' | 'medium' | 'high'
      tag?: string
    }): Todo[];
  updateTodo(id: string, data: Partial<Omit<Todo, 'id' | 'userId' | 'createdAt'>>): Todo | undefined;
  deleteTodo(id: string): boolean;
  completeTodo(id: string): Todo | undefined;
  getStats(userId: string): {
    total: number
    completed: number
    pending: number
  };
  getOverdueTodos(userId: string): Todo[];
}
